import React, { useState } from "react";
import { Container, Col, Card, CardGroup, Row } from "react-bootstrap";
import { generateQuiz } from "../utils/API";

export default function Quiz() {
    const [quizData, setQuizData] = useState([]);

    const handleFormSubmit = async (event) => {

        event.preventDefault();
        try {

            await generateQuiz()
            .then((response) => response.json())
            // .then((data) => setQuizData(JSON.parse(JSON.stringify(data.results).replace(/&quot;/g, "''").replace(/&#039;/g, "'"))))
            .then((data) => setQuizData(JSON.parse(JSON.stringify(data.results).r)))
        } catch (error) {
            console.error(error);
        }
    }

    return (
      <Container>
        <button onClick={handleFormSubmit}>Submit</button>
        {quizData.map((quiz) => {
          return (
            <CardGroup>
              <Row>
                <Col>
                  <Card border="dark">
                    <Card.Body>
                      <Card.Title className="text-center card-title">
                        {quiz.question}
                      </Card.Title>

                      <Card.Text>
                        {quiz.correct_answer}
                        {quiz.incorrect_answers[0]}
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </CardGroup>
          );
        })}
      </Container>
  );
}
