import React, { useState } from "react";
import { Container, Col, Card, CardGroup, Row, Button } from "react-bootstrap";
import { generateQuiz } from "../utils/API";

export default function Quiz() {
    const [quizData, setQuizData] = useState([]);
    const [answersArray, setAnswersArray] = useState([]);

    const handleFormSubmit = async (event) => {

        event.preventDefault();
        try {

            await generateQuiz()
            .then((response) => response.json())
            .then((data) => setQuizData(JSON.parse(JSON.stringify(data.results).replace(/&quot;/g, "''").replace(/&#039;/g, "'"))))

        } catch (error) {

            console.error(error);

        }
    }

    const handleClickAnswer = (event) => {

        event.target.id
    }

    return (
      <Container>
        <button onClick={handleFormSubmit}>Submit</button>
        {quizData.map((quiz,index) => {
          return (
            <CardGroup>
              <Row>
                <Col>
                  <Card border="dark" id={index+"QuestionCard"}>
                    <Card.Body>
                      <Card.Title className="text-center card-title">
                        {quiz.question}
                      </Card.Title>

                      <Card.Text>
                        <button className="btn btn-primary" id={index+"TrueAnswer"} onClick={handleClickAnswer}>True</button>
                        <button className="btn btn-primary" id={index+"FalseAnswer"} onClick={handleClickAnswer}>False</button>

                        {/* <Button className="btn btn-primary">True</Button> */}
                        {/* <Button className="btn btn-primary">False</Button> */}
                        {/* {quiz.correct_answer} */}
                        {/* {quiz.incorrect_answers[0]} */}
                      </Card.Text>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            </CardGroup>
          );
        })}
      </Container>
  );
}
